import React from "react";
import set from "lodash/fp/set"; //얘는 지우고
import {Field} from "redux-form";
// import Table from "react-table";
import Table from "react-table"
import * as BS from "react-bootstrap";
import initialData from "./dataFactory";
import FormProvider from "./FormProvider";
import {avatarColumnProps} from "./AvatarCell";
import ActionsCell from "./ActionsCell";
import HighlightCell from "./HighlightCell";
import GridFilters from "./GridFilters";
import {useSelector} from "react-redux";
import {connect} from "react-redux"
import fetchAPI from "../../services/Redux/reducers/fetchAPI";
import 'react-table/react-table.css';
import {
    addedField,
    currEd,
    deleteTmp,
    getAllR,
    hisFlag,
    keyFieldSelect,
    openSelectKeyFieldPop, selectHead
} from "../../services/Redux/actions";
import {bindActionCreators} from 'redux';
import {addTmp, revRe, openPop} from "../../services/Redux/actions";
import Pagination from "./Pagination";
import {apiProvider} from "../../services/Provider";
import "./style.scss"
import SelectKeyFieldPopup from "../popup/SelectKeyFieldPopup";
import SelectConfirmsPopup from "../popup/SelectConfirmsPopup";
import _, {debounce} from "lodash"


//TODO:: 이 테이블들.. 나중에 합칠거임.
class GridTableNormal extends React.Component {
    state = {
        editing: null,
        selected: {},
        selectAll: 0,
        adding: false,
        columns: [],
        selectEdit: false,
        willRemoved: [],
        temps: false,
        pages: null,
        pagesH: null,
        loading: true,
        sorted: [],
        totalRows: 0,
        tableProps: {}
    };

    selectList = {
        typeList: ["String", "Numeric", "IP"],
        groupList: ["Required", "Additional"]
    }

    //TODO:: Livedetectrule 처럼 필드가 유동적이진 않아서 이렇게 안해도됨. 고쳐야할 것
    resetColumn = () => {
        this.setState((state) => {
            return {
                ...state, columns:
                    [{
                        columns: [
                            {
                                id: "checkbox",
                                accessor: "",
                                Cell: ({original}) => {
                                    return (
                                        <input
                                            type="checkbox"
                                            className="checkbox"
                                            checked={this.state.selected[original.id] === true}
                                            onChange={() => this.toggleRow(original.id)}
                                        />
                                    );
                                },
                                Header: x => {
                                    return (
                                        <input
                                            type="checkbox"
                                            className="checkbox"
                                            checked={this.state.selectAll === 1}
                                            ref={input => {
                                                if (input) {
                                                    input.indeterminate = this.state.selectAll === 2;
                                                }
                                            }}
                                            onChange={() => this.toggleSelectAll()}
                                        />
                                    );
                                },
                                sortable: false,
                                width: 45
                            },
                        ]
                    }].concat(
                        this.props.columns.slice().map((item) => {
                            console.log(item)
                            return item.accessor !== "vendors" ? {...item, ...this.editableColumnProps} : {...item}
                        })
                    )
                        .concat(
                            {
                                Header: "",
                                maxWidth: 90,
                                filterable: false,
                                getProps: this.getActionProps,
                                Cell: ActionsCell
                            }
                        )
            }
        })
    }
    getSelect = (type, curr) => {
        const v = this.selectList[type].filter(i => i !== curr)
        return v
    }

    componentDidMount() {

        apiProvider.getRowCounts().then((res) => {
            this.setState({totalRows: res});
            console.log(res)
        })
        this.resetColumn()
        // this.props.handleData()
    }

    tP =
        {
                manual: true,
                onFetchData: this.fetchData,
                pages: this.state.pages
        }
    componentDidUpdate = (prevProps) => {
        const ed = (prevProps.edited !== this.props.edited)
        const cond = (prevProps.refre !== this.props.refre)
        // const his = (this.props.hflag && (prevProps.hflag !== this.props.hflag))
        const temp = (prevProps.temps !== this.state.temps)
        const page = (this.state.pagesH == 0 && (prevProps.pagesH !== this.state.pagesH))
        if (cond) {
            this.setState({editing: null})
            this.props.handleData()
        }
        if (temp && this.state.temps) {
            apiProvider.setHistory().then(() => this.props.hisFlag(true))
            this.setState({temps: false})
        }
        if (ed) { //TODO::이게 빨리 안되나보지?
            console.log(this.state.pagesH)
            this.setState({pagesH: 0})
        }
        if (page) {
            this.tp = {

                    manual: false,
                    page: this.state.pagesH,
                    onPageChange: (page) => {
                        this.setState({pagesH: page});
                        console.log(this.state.pagesH)
                    }
            }

        }
    }

    selectableComponent = ({input, editing, value, ...rest}) => {
        //TODO:: 이부분이 셀 filtering 하는 부분 인가?
        const result = Object.keys(rest).reduce((prev, current) =>
            ({...prev, [current.toLowerCase()]: rest[current]}), {})
        const Component = editing ? 'select' : 'p';
        const children =
            (!editing && <HighlightCell value={value} {...rest} />) || <React.Fragment>
                <option>{value}</option>
                {this.getSelect("typeList", value).map(
                    (item, index) => (
                        <option key={index} value={item}>{item}</option>
                    )
                )}
            </React.Fragment>;
        return <Component {...input} {...result} children={children}/>;
    };


    editableComponent = ({input, editing, value, ...rest}) => {

        const result = Object.keys(rest).reduce((prev, current) =>
            ({...prev, [current.toLowerCase()]: rest[current]}), {})
        const Component = editing ? 'input' : 'p';
        const children =
            (!editing && <HighlightCell value={value} {...rest} />) || undefined;
        return <Component {...input} {...result} children={children}/>;
    };

    isSelectField = (col) => ["type", "group"].indexOf(col) !== -1

    editableColumnProps = {
        ...GridFilters,
        Cell: (props) => {
            const editing = this.state.editing === props.original;
            const fieldProps = {
                component: this.isSelectField(props.column.id) ? this.selectableComponent : this.editableComponent,
                editing,
                props
            };
            return <Field name={props.column.id} {...fieldProps} />;

        }
    };

    getActionProps = (gridState, rowProps) =>
        (rowProps && {
            mode: this.state.editing === rowProps.original ? (this.state.adding ? "add" : "edit") : "view",

            actions: {
                onEdit: () => {
                    !this.state.adding && this.setState({editing: rowProps.original, adding: false})
                },
                onCancel: () => {
                    this.setState({editing: null, adding: false});
                    this.state.adding && this.props.delTmp(this.props.data.length)
                }
            }
        }) ||
        {};

    toggleRow(id) {
        const newSelected = Object.assign({}, this.state.selected);
        newSelected[id] = !this.state.selected[id];
        this.setState({
            selected: newSelected,
            selectAll: 2
        });
        console.log(this.state.selected)
    }

    toggleSelectAll() {
        let newSelected = {};
        console.log(this.state.selected)
        if (this.state.selectAll === 0) {
            this.props.data.forEach(x => {
                newSelected[x.id] = true;
            });
        }

        this.setState({
            selected: newSelected,
            selectAll: this.state.selectAll === 0 ? 1 : 0
        });
    }

    handleSubmit = (values) => {
        this.setState((state) => {
            this.props.addTmp(this.props.data.map((item) => {
                return item.id === values.id ? values : item
            }))

            return state.adding ? {...state, adding: false} : {...state}

        });
    };

    async handleSaveAfterDelete() {
        return await Promise.all(this.props.data.map((item) =>
            item.hasOwnProperty("rules") ? this.props.handleAddTotal(item) : this.props.handleAdd(item)
        ))
    }

    handleSave = () => {
        this.state.willRemoved.length && this.state.willRemoved.forEach((id) => {
            apiProvider.delNormal(parseInt(id)).then(() => this.setState({willRemoved: []}))
        })
        this.handleSaveAfterDelete().then(() => this.setState({temps: true}))

    }

    handleDelete = () => {
        Object.keys(this.state.selected).forEach((i) =>
            this.props.delTmp(i)
        )
        this.setState((state) => {
            return {
                ...state,
                selected: {},
                willRemoved: [...Object.keys(this.state.selected), ...this.state.willRemoved]
            }
        })


    }

    handleAdd = () => {
        const addTemp = this.state.columns.slice()
        addTemp.splice(-1, 1)
        addTemp.splice(0, 1)
        const tp = {};
        addTemp.forEach(({accessor}) => {
            tp[accessor] = ""
        })
        tp["rules"] = []
        tp.id = Math.max(0, ...this.props.data.slice().map(v => v.id)) + 1 //TODO:: => api 근데 왜 이렇게 하면 안됐더라?

        this.setState((state) => {
            return {...state, editing: tp, adding: true}
        })
        console.log(tp)
        this.props.addTmp([tp, ...this.props.data])
    }


    handleClickPopUp = (columnId, data) => {
        console.log(this)
        this.props.handleRow(data) //id->classifier(PK)
    }

    sendWithQuery = (filtered, offset, limit, pageSize) => {
        const ob = {}
        filtered.forEach(({id, value}) => {
            ob[id] = value
        })
        const obj = {
            offset: offset,
            limit: limit,
            filters: ob
        }
        //TODO:: for filtered 이거 async 라 추후 초치 필요할듯
        apiProvider.getFiltersCounts(obj).then((res) => this.setState({totalRows: res}))
        //TODO:: debounce it
        this.props.getDataFilter(obj).then((res) => {
            this.setState({loading: false, pages: Math.ceil(this.state.totalRows / pageSize)})
        })
    }

    fetchData = (state, instance) => {

        apiProvider.getRowCounts().then((res) => this.setState({totalRows: res})) //TODO::이거 동기 맞춰야함.
        this.setState({loading: true})
        const offset = state.page * state.pageSize
        console.log(state.page)
        const limit = state.pageSize

        state.filtered.length ? (
            this.sendWithQuery(state.filtered, offset, limit, state.pageSize)
            //TODO:: 앞에 handleData 랑 아구 맞추어 놓을것~ 아마 componentdidmount에서 없어도 될것임.
        ) : (
            this.props.getData(offset, limit).then((res) => {
                this.setState({loading: false, pages: Math.ceil(this.state.totalRows / state.pageSize)}) //totalRows 도 같이 받아왔으면 좋겠다
            }))
    }

    render() {

        console.log(this.props.data)
        return (

            <React.Fragment>

                <div className="btns">
                    <button className="btn" onClick={this.handleAdd}>
                        Add
                    </button>
                    <button className="btn" onClick={this.handleDelete}>
                        Delete
                    </button>
                    <button className="btn" onClick={() => this.props.revRe(true)}>
                        Refresh
                    </button>

                    <div className="seperate-bar-100"/>

                    <button className="btn" onClick={this.handleSave}>
                        Save
                    </button>
                    <button className="btn" onClick={() => this.props.openPop(true)}>
                        배포
                    </button>
                </div>
                <div className="table-wrapper">
                    <div className="Fixed">

                        <FormProvider
                            form="inline"
                            onSubmit={this.handleSubmit}
                            onSubmitSuccess={() => this.setState({editing: null})}
                            initialValues={this.state.editing}
                            enableReinitialize
                        >
                            {(formProps) => {
                                return (
                                    <form onSubmit={formProps.handleSubmit}>
                                        <Table
                                            getTdProps={(state, rowInfo, column) => ({
                                                onClick: () => !this.state.editing ? this.handleClickPopUp(column.id, rowInfo.original) : undefined //TODO::nullcheck 편집중에 다른 행 만지면 클남..
                                            })}
                                            columns={
                                                this.state.columns
                                            }
                                            data={this.props.data}
                                            defaultPageSize={2}
                                            {...this.tP}
                                            // manual

                                            // pages={this.state.pages}
                                            // pageSize={this.state.pageSize
                                            // onFetchData={this.fetchData}
                                            // filterable
                                            // onFilteredChange={(onFilterChange)=>debounce(onFilterChange, 1000)}
                                            sorted={[{id: 'id'}]}
                                        />
                                        {this.props.selectKPop && <SelectKeyFieldPopup></SelectKeyFieldPopup>}
                                        {this.props.selectCPop && <SelectConfirmsPopup></SelectConfirmsPopup>}

                                    </form>
                                );
                            }}
                        </FormProvider>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default connect(
    state => ({
        version: state.fetchAPI.headVersion,
        selectKPop: state.fetchAPI.openKeyField,
        selectCPop: state.fetchAPI.openConField,
        kfOb: state.fetchAPI.keyField,
        data: state.fetchAPI.data,
        refre: state.fetchAPI.refre,
        add: state.fetchAPI.ad,
        hflag: state.fetchAPI.hflag,
        newField: state.fetchAPI.addedField,
        edited: state.fetchAPI.edited
    }),
    dispatch => {
        return bindActionCreators(
            {
                getData: apiProvider.getNormalizeRule,
                getDataFilter: apiProvider.getNormalizeRuleFilter,
                setKfOb: keyFieldSelect,
                addTmp: addTmp,
                revRe: revRe,
                openPop: openPop,
                delTmp: deleteTmp,
                currEd: currEd,
                hisFlag: hisFlag,
                addedField: addedField,
                setHead: selectHead
            },
            dispatch
        )
    }
)(GridTableNormal)
