import React from "react";
import set from "lodash/fp/set"; //얘는 지우고
import {Field} from "redux-form";
// import Table from "react-table";
import Table from "react-table"
import * as BS from "react-bootstrap";
import initialData from "./dataFactory";
import FormProvider from "./FormProvider";
import {avatarColumnProps} from "./AvatarCell";
import ActionsCell from "./ActionsCell";
import HighlightCell from "./HighlightCell";
import GridFilters from "./GridFilters";
import {useSelector} from "react-redux";
import {connect} from "react-redux"
import fetchAPI from "../../services/Redux/reducers/fetchAPI";
import {
    addedField,
    currEd,
    deleteTmp,
    getAllR,
    hisFlag,
    keyFieldSelect,
    openSelectKeyFieldPop, restoreHead, restoreVersion, saveVersion, selectHead, startHis
} from "../../services/Redux/actions";
import {bindActionCreators} from 'redux';
import {addTmp, revRe, openPop} from "../../services/Redux/actions";
import Pagination from "./Pagination";
import {apiProvider} from "../../services/Provider";
import "./style.scss"
import SelectKeyFieldPopup from "../popup/SelectKeyFieldPopup";
import SelectConfirmsPopup from "../popup/SelectConfirmsPopup";


class GridTableLDR extends React.Component {
    state = {editing: null, selected: {}, selectAll: 0, adding: false, columns: [], selectEdit: false};

    selectList = {
        typeList: ["String", "Numeric", "IP"],
        groupList: ["Required", "Additional"]
    }

    getSelect = (type, curr) => {
        const v = this.selectList[type].filter(i => i !== curr)
        return v
    }

    resetColumn = () => {
        this.setState((state) => {
            return {
                ...state, columns:
                    [{
                        columns: [
                            {
                                id: "checkbox",
                                accessor: "",
                                Cell: ({original}) => {
                                    return (
                                        <input
                                            type="checkbox"
                                            className="checkbox"
                                            checked={this.state.selected[original.id] === true}
                                            onChange={() => this.toggleRow(original.id)}
                                        />
                                    );
                                },
                                Header: x => {
                                    return (
                                        <input
                                            type="checkbox"
                                            className="checkbox"
                                            checked={this.state.selectAll === 1}
                                            ref={input => {
                                                if (input) {
                                                    input.indeterminate = this.state.selectAll === 2;
                                                }
                                            }}
                                            onChange={() => this.toggleSelectAll()}
                                        />
                                    );
                                },
                                sortable: false,
                                width: 45
                            },
                        ]
                    }].concat(
                        this.props.columns.slice().map((item) => {
                            return {...item, ...this.editableColumnProps}
                        })
                    )
                        .concat(
                            {
                                Header: "",
                                maxWidth: 90,
                                filterable: false,
                                getProps: this.getActionProps,
                                Cell: ActionsCell
                            }
                        )
            }
        })
    }

    componentDidMount() {
//for test
        this.resetColumn()
        this.props.handleData()
    }

    componentDidUpdate = (prevProps) => {
        const ver = (prevProps.version !== this.props.version)
        const resHead = (prevProps.headRestore !== this.props.headRestore)
        const cond = (prevProps.refre !== this.props.refre) //addtmp 바꾸ㅕㅅ을때
        const nField = (prevProps.newField !== this.props.newField)
        const col = (prevProps.header !== this.props.header)
        const his = (prevProps.his !== this.props.his)
        // if(this.props.his && his){
        //     console.log("not here")
        //     const history = {
        //         id: "1",
        //         desc: "temp",
        //         user: "admin",
        //         released: "false",
        //         value: {field: this.props.data} //props 수정이 덜 되어서..
        //     }
        //     apiProvider.insert('history/livedetectrule', history).then(() => {
        //         this.props.hisFlag(true)
        //         this.props.startHis(false)
        //     })
        // }
        if (col) {
            this.resetColumn()
        }
        if (cond) {
            // this.props.handleHead()
            console.log(this.props.version)//헤더 다시 세
            this.props.handleHeadVer(this.props.version)
            this.resetColumn()
            this.props.handleData() //이거 없어도 될거같은데
        }
        if (resHead && this.props.headRestore) { //restore 버튼
            console.log(this.props.data[0].ver)
            this.props.restoreVer(this.props.data[0].ver)
            this.props.handleHeadVer(this.props.data[0].ver)
            this.props.resHead(false)
        }팅
        if (nField) {
            const nCol = this.state.columns
            nCol.splice(-1, 0, {
                Header: this.props.newField,
                accessor: this.props.newField, ...this.editableColumnProps
            })
            this.setState((state) => {
                return {
                    ...state, columns: nCol
                }
            })
        }
        if (ver) {
            console.log("hi?")
            this.handleSaveNewVer()
        }
    }


    selectableComponent = ({input, editing, value, ...rest}) => {

        const result = Object.keys(rest).reduce((prev, current) =>
            ({...prev, [current.toLowerCase()]: rest[current]}), {})
        const Component = editing ? 'select' : 'p';
        const children = //여기 value 에 직접 넣을 수 있으면 좋을텐데.
            (!editing && <HighlightCell value={value} {...rest} />) || <React.Fragment>
                <option>{value}</option>
                {this.getSelect("typeList", value).map(
                    (item, index) => (
                        <option key={index} value={item}>{item}</option>
                    )
                )}
            </React.Fragment>;
        return <Component {...input} {...result} children={children}/>;
    };


    editableComponent = ({input, editing, value, ...rest}) => {

        const result = Object.keys(rest).reduce((prev, current) =>
            ({...prev, [current.toLowerCase()]: rest[current]}), {})
        //input안에 name있음 이거 가지고 action넣으면 되지 않을까
        const Component = editing ? 'input' : 'p';
        const children =
            (!editing && <HighlightCell value={value} {...rest} />) || undefined;
        return <Component {...input} {...result} children={children}/>;
    };

    isSelectField = (col) => ["type", "group"].indexOf(col) !== -1

    editableColumnProps = {
        ...GridFilters,
        Cell: (props) => {
            const editing = this.state.editing === props.original;
            const fieldProps = {
                component: this.isSelectField(props.column.id) ? this.selectableComponent : this.editableComponent,
                editing,
                props
            };
            return <Field name={props.column.id} {...fieldProps} />;

        }
    };

    handleCellClick = (data) => {
        // this.props.handleRow(data)
    }

    getActionProps = (gridState, rowProps) =>
        (rowProps && {
            mode: this.state.editing === rowProps.original ? (this.state.adding ? "add" : "edit") : "view",

            actions: {
                onEdit: () => {
                    !this.state.adding && this.setState({editing: rowProps.original, adding: false})
                },
                onCancel: () => {
                    this.setState({editing: null, adding: false});
                    this.state.adding && this.props.delTmp(this.props.data.length)
                }
            }
        }) ||
        {};

    toggleRow(id) {
        const newSelected = Object.assign({}, this.state.selected);
        newSelected[id] = !this.state.selected[id];
        this.setState({
            selected: newSelected,
            selectAll: 2
        });
        console.log(this.state.selected)
    }

    toggleSelectAll() {
        let newSelected = {};
        console.log(this.state.selected)
        if (this.state.selectAll === 0) {
            this.props.data.forEach(x => {
                newSelected[x.id] = true;
            });
        }

        this.setState({
            selected: newSelected,
            selectAll: this.state.selectAll === 0 ? 1 : 0
        });
    }

    handleSubmit = (values) => {
        console.log("why not cancel?") //TODO::cancel이 일로가는구나 버튼이 작아서 잘리네 잘 눌러야함..
        this.setState((state) => {
            this.props.addTmp(this.props.data.map((item) => {
                return item.id === values.id ? values : item
            }))

            return state.adding ? {...state, adding: false} : {...state}

        });
    };

    handleConFieldSelect = (ob) => {
        this.props.setKfOb(ob) //TODO::
        this.props.openConSelectPopup()
    }
    handleKeyFieldSelect = (ob) => {
        console.log(ob)
        this.props.setKfOb(ob)
        this.props.openKeySelectPopup()
    }

    handleFieldAdd = () => {
        this.props.openSelectPopup()
    }

    handleGridData = async (version) => {
        apiProvider.delAll('rules').then(
            await Promise.all(this.props.data.map((item) => { //사실 여기서 버전을 제외하고는 업데이트가 되어 있을 것.
                this.props.addf(item, version)
            }))
        )

        console.log(this.props.data.map(({ver, ...other})=>{
            return Object.assign({}, other, {ver:version})
        }))
        const history = {
            id: "1",
            desc: "temp",
            user: "admin",
            released: "false",
            value: {
                field: this.props.data.map(({ver, ...other})=>{
                    return Object.assign({}, other, {ver:version})
                })
            } //props 수정이 덜 되어서..
        }
        console.log(history)
        apiProvider.insert('history/livedetectrule', history).then(() => {
            this.props.hisFlag(true)
        })

        console.log(this.props.data)
    }

    handleSaveNewVer = () => {
        console.log(this.props.version)
        const temp = this.state.columns.slice()
        temp.splice(0, 1)
        temp.splice(-1, 1)
        console.log(temp.map(({Header}) => Header))
        const header = {
            ver: this.props.version,
            type: "live",
            header: temp.map(({Header}) => Header)
        }
        // apiProvider.insertHead(header).then()
        apiProvider.deActiveHead().then(
            () => apiProvider.insertHead(header).then(() => this.handleGridData(this.props.version))
        )
        // this.props.insertHead(header)
        // this.handleGridData()
    }

    handleSaveSameVer = (version) => {
        // this.props.activeHead(version)
        apiProvider.deActiveHead().then(
        apiProvider.activeHead(version).then(
            () => this.handleGridData(version) //TODO:: 이거 하고 active 된 header update 정보를 최신으로 바꾸던지, + 아무것도 안하고 저장하는게 없네
        ))
        // this.handleGridData()
    }
    //체크해야 되는게 291라인과 298라인 아닌가?
    handleSave = () => {
        // const temp = this.state.columns
        // temp.splice(0, 1)
        // temp.splice(-1, 1)
        //밑에 조건 둘 중 무조건 하나만 실행됨. 그래야함..
        if (this.props.columns.length != this.state.columns.length - 2) this.props.openSave()
        else if (this.props.versionTmp !== "") this.handleSaveSameVer(this.props.versionTmp)
        else this.handleGridData(this.props.version)

    }

    handleDelete = () => {
        console.log(Object.keys(this.state.selected))
        Object.keys(this.state.selected).forEach((i) =>
            this.props.delTmp(i)
        )
        this.setState((state) => {
            return {...state, selected: {}}
        })

    }

    //TODO::add 여러번 하게하면 안됨. + 자동으로 index 생성되게 해야되는데 + livedetectrule add row가 이상한곳에 생김. 왠진 모르겠는데 해결됨.
    handleAdd = () => {
        const addTemp = this.state.columns.slice()
        addTemp.splice(-1, 1)
        addTemp.splice(0, 1)
        const tp = {};
        addTemp.forEach(({accessor}) => {
            tp[accessor] = ""
        })
        tp.id = this.props.data.length + 1
        console.log(this.props.data.length)
        this.setState((state) => {
            return {...state, editing: tp, adding: true}
        })
        console.log(tp)
        this.props.addTmp([tp, ...this.props.data])
    }


    handleClickPopUp = (columnId, data) => {
        switch (columnId) {
            case "keyField" :
                return this.handleKeyFieldSelect(data)
            case "confirms" :
                return this.handleConFieldSelect(data)
            default:
                return
        }
    }

    render() {
        return (
            <React.Fragment>

                <div className="btns">
                    <button className="btn" onClick={this.handleAdd}>
                        Add
                    </button>
                    <button className="btn" onClick={this.handleDelete}>
                        Delete
                    </button>
                    <button className="btn" onClick={() => this.props.revRe(true)}>
                        Refresh
                    </button>

                    <div className="seperate-bar-100"/>

                    <button className="btn" onClick={() => {
                        this.handleFieldAdd()
                    }}>
                        Field Add
                    </button>
                    <button className="btn" onClick={this.handleSave}>
                        Save
                    </button>
                    <button className="btn" onClick={() => this.props.openPop(true)}>
                        배포
                    </button>
                </div>
                <div className="table-wrapper">
                    {/*<div className="table-total-count" style={{ fontSize: '12px' }}>*/}
                    {/*  Total:*/}
                    {/*</div>*/}
                    <div className="Fixed">

                        <FormProvider
                            form="inline"
                            onSubmit={this.handleSubmit}
                            onSubmitSuccess={() => this.setState({editing: null})}
                            initialValues={this.state.editing}
                            enableReinitialize
                        >
                            {(formProps) => {
                                return (
                                    <form onSubmit={formProps.handleSubmit}>
                                        <Table
                                            getTdProps={(state, rowInfo, column) => ({
                                                onClick: () => (rowInfo !== undefined && this.state.editing) ? this.handleClickPopUp(column.id, rowInfo.original) : undefined
                                            })}
                                            columns={
                                                this.state.columns
                                            }
                                            data={this.props.data}
                                            // PaginationComponent={Pagination}
                                            pageSizeOptions={[5, 10, 500, 1000]}
                                            defaultPageSize={10}
                                            sorted={[{id: 'id'}]}

                                            // defaultPageSize={10}
                                        />
                                        {this.props.selectKPop && <SelectKeyFieldPopup></SelectKeyFieldPopup>}
                                        {this.props.selectCPop && <SelectConfirmsPopup></SelectConfirmsPopup>}
                                    </form>
                                );
                            }}
                        </FormProvider>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default connect(
    state => ({
        version: state.fetchAPI.headVersion,
        versionTmp: state.fetchAPI.headVersionTmp,
        selectKPop: state.fetchAPI.openKeyField,
        selectCPop: state.fetchAPI.openConField,
        kfOb: state.fetchAPI.keyField,
        data: state.fetchAPI.data,
        refre: state.fetchAPI.refre,
        add: state.fetchAPI.ad,
        hflag: state.fetchAPI.hflag,
        newField: state.fetchAPI.addedField,
        header: state.fetchAPI.header,
        headRestore: state.fetchAPI.headRestore,
        his: state.fetchAPI.startHistory
    }),
    dispatch => {
        return bindActionCreators(
            {
                setKfOb: keyFieldSelect,
                addTmp: addTmp,
                revRe: revRe,
                openPop: openPop,
                delTmp: deleteTmp,
                currEd: currEd,
                hisFlag: hisFlag,
                addedField: addedField,
                setHead: selectHead,
                resHead: restoreHead,
                saveVer: saveVersion,
                restoreVer: restoreVersion,
                startHis: startHis
            },
            dispatch
        )
    }
)(GridTableLDR)
