import React, {useEffect, useState} from "react";
import TableContainer from "@material-ui/core/TableContainer";
import Paper from "@material-ui/core/Paper";
import Table from "@material-ui/core/Table";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import TableCell from "@material-ui/core/TableCell";
import TableSortLabel from "@material-ui/core/TableSortLabel";
import {Checkbox, TablePagination, Tooltip} from "@material-ui/core";
import TableBody from "@material-ui/core/TableBody";
import Button from "@material-ui/core/Button";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import {makeStyles} from "@material-ui/core/styles";
import {useDispatch, useSelector} from "react-redux";
import {getAllR, revRe} from "../services/Redux/actions";
import {apiProvider} from "../services/Provider";
import {revOp, clickedOp} from "../services/Redux/actions";

const useStyles2 = makeStyles({
    table: {
        minWidth: 650,
        // display: "flex"
    },
    tableHead: {
        display:"flex",
        flexDirection:"column",
    },
    tableBody: {
        display:"flex",

    },
    tableRow: {
        display:"flex",
        flexDirection:"column"
    }

});

function BasicTable(props) {
    const classes = useStyles2();
    const [selected, setSelected] = useState([])
    const dispatch = useDispatch();

    const results = useSelector(state => state.fetchAPI);
    //props.data => results.data
    const [state, setState] = useState({
        order: "asc",
        orderBy: "name",
        selected1:[],
        page:0,
        rowsPerPage:5
    })
    //custom hook
    function useEffectSkipFirst(fn, arr){
        const isFirst = React.useRef(true);

        useEffect(()=>{
            if(isFirst.current){
                isFirst.current = false;
                return;
            }
            return fn();
        }, arr);
    }

    useEffectSkipFirst ( ()=>{ //이거 SkipFirst 해도 되는지 다시 확인요망
        // const type = results.currType
        // dispatch(apiProvider.getAllRedux(type))
        props.handleData()
    }, [results.ad, results.refre, results.currType])

    useEffectSkipFirst(()=>{
        if(props.delete) setSelected([])
    },[props.delete])


    const handleRequestSort = (event, property) => {
        const orderBy = property;

        let order = "desc";
        if( state.orderBy === property && state.order === "desc") {
            order = "asc";
        }

        setState({...state, order:order, orderBy: orderBy})
    };


    const handleChangePage = (event, newpage) => {
        setState({...state, page:newpage});

    };
    const handleChangeRowsPerPage = event => {
        setState({...state, page:0, rowsPerPage:parseInt(event.target.value)})

    }

    const handleSelectAllClick = (event, checked) => {
        if (checked) {
            setSelected(results.data.map(n=>n.id));
            props.selectedUp(results.data.map(n=>n.id));

            return;
        }
        setSelected([]);
        props.selectedUp([]);


    };

    const handleClick = (event, id) => {

        const selectedIndex = selected.indexOf(id);
        let newSelected = [];

        if (selectedIndex === -1) {
            newSelected = newSelected.concat(selected, id);
        } else if (selectedIndex === 0) {
            newSelected = newSelected.concat(selected.slice(1));
        } else if (selectedIndex === selected.length - 1) {
            newSelected = newSelected.concat(selected.slice(0, -1));
        } else if (selectedIndex > 0) {
            newSelected = newSelected.concat(
                selected.slice(0, selectedIndex),
                selected.slice(selectedIndex + 1)
            );
        }

        setSelected(newSelected);

        props.selectedUp(newSelected);

    };
    console.log(selected)
    const isSelected = id => selected.indexOf(id) !== -1;

    const handleCellClick = (e, row) => {
        // apiProvider.getRow(results.currType, row.id).then((data)=>dispatch(clickedOp(data.data, true)))
        props.handleCell && props.handleCell(e, row)

    }
    const createSortHandler = property => event => {
        handleRequestSort(event, property)
    }

    function getSorting(order, orderBy) {
        return order === "desc"
            ? (a, b) => (b[orderBy] < a[orderBy] ? -1 : 1)
            : (a, b) => (a[orderBy] < b[orderBy] ? -1 : 1);

    }

    const {order, orderBy, selected1 ,rowsPerPage, page} = state;
    const emptyRows = rowsPerPage - Math.min(rowsPerPage, results.data.length - page * rowsPerPage)
    return (
        results.data.length > 0 ?
        <Paper>
            <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="simple table">
                    <TableHead>

                        <TableRow>
                            <TableCell>
                                <Checkbox
                                    color="primary"
                                    inputProps={{ 'aria-label': 'secondary checkbox' }}
                                    onChange={handleSelectAllClick}

                                />
                            </TableCell>
                            {Object.keys(results.data.find(Boolean)).filter((i)=> ["id", "createdAt", "updatedAt", "props"].indexOf(i) < 0).map((item, index) => (
                                <TableCell

                                    align="right"
                                    key={item}
                                    sortDirection={state.orderBy === item ? state.order : false}
                                >
                                    <Tooltip
                                        title="Sort"
                                        placement={"bottom-start"}
                                        enterDelay={300}
                                    >
                                        <TableSortLabel
                                            active={state.orderBy === item}
                                            direction={state.order}
                                            onClick={createSortHandler(item)}
                                        >
                                            {item}
                                        </TableSortLabel>

                                    </Tooltip>
                                </TableCell>
                            ))}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {results.data.filter(value=>{
                            return (
                                props.val.length > 0 ? (value.name.toLowerCase().includes(props.val.toLowerCase())) : (value)
                            );
                        }).sort(getSorting(state.order, state.orderBy))
                            .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                            .map((item, index) => {
                                const isSelected2 = isSelected(item.id);
                                return (
                                    <TableRow key={index} >
                                        <TableCell component="th" scope="row" /*onClick={ HandleCheckBox }*/>
                                            <Checkbox
                                                checked={isSelected2}
                                                color="primary"
                                                inputProps={{ 'aria-label': 'secondary checkbox' }}
                                                selected={isSelected2}
                                                onClick={event=>handleClick(event,item.id)}
                                            />
                                        </TableCell>

                                        {Object.keys(results.data.find(Boolean)).filter((i)=> ["id", "createdAt", "updatedAt", "props"].indexOf(i) < 0).map((itemKey, index) => (

                                            <TableCell key={index} onClick={(event)=>handleCellClick(event, item)}>{item[itemKey]}</TableCell>
                                        ))}
                                        <TableCell align="right">
                                            <Button>
                                                <MoreVertIcon>
                                                </MoreVertIcon>

                                            </Button>


                                        </TableCell>
                                    </TableRow>
                                )})}
                        {emptyRows > 0 && (
                            <TableRow style={{height:53 * emptyRows}}>
                                <TableCell colSpan={6} />
                            </TableRow>
                        )}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[5, 10, 25]}
                component="div"
                count={results.data.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
            />

        </Paper> : <span>No Data</span>
    );

}
export default React.memo(BasicTable)
