const initialState = {
    addId: [],
    delId: [],
    editId: [],
    addData: [{}],
    editData: [{}]
}

export default function editOperator(state = initialState, action ){
    switch (action.type) {
        case "ADD_SAVE":
            return {
                ...state,
                addId: action.ids,
                addData: action.data
            }
        case "DEL_SAVE":
            return {
                ...state,
                delId: action.ids
            }
        case "EDIT_SAVE":
            return {
                ...state,
                editId: action.ids,
                editData: action.data
            }
        default:
            return state;
    }
}
